
import pdfMake from 'pdfmake/build/pdfmake';
import pdfFonts from 'pdfmake/build/vfs_fonts'; // Ensure you import fonts

pdfMake.vfs = pdfFonts.pdfMake.vfs;

const converter = require('number-to-words');

const STATIC_FIELDS = {
    header1: "Gajanan Maharaj America Devotees Parivar",
    subheader1: "Donation Statement",
    messageBody1: "Jai Gajanan!",
    messageBody2: "We sincerely appreciate your support. Your timely contribution will help preserve our Indian legacy, and help us to " +
        "pass our spiritual legacy to future generation.",
    messageBody3: "Our humble prayer to Shri Gajanan Maharaj (Shegaon) to bless you and your family with peace, prosperity and " +
        "happiness.",
    messageBody4: "Listed below are the details of your contribution for ",
    receiptDate: "Receipt Date",
    donationTypes: "Cash/Online/CC/Check",
    donationDate: "Donation Date",
    purpose: "Purpose",
    amount: "Amount (USD)",
    thanksMessage: "Thank you for Donation",
    disclaimer: "Gajanan Maharaj America Devotees Parivar Inc. EIN: 82-4152472. No goods or services provided in exchange of " +
        "this donation. This is your official receipt for tax purposes. Upon completion of a program specified for support, " +
        "surplus fund directed toward that program may be used for other activities as directed by its Board of Directors." +
        "www.gajananmaharajamerica.org",
    total: '************ Total ***********'
};

const logo = "data:image/png;base64,"; 

function titleCase(str) {
    return str.toLowerCase().split(/[- ]/).map(word => word.charAt(0).toUpperCase() + word.slice(1)).join(' ');
}

function getTextFromAmount(amount) {
    // Ensure amount is a string
    const amountStr = String(amount);
    
    // Remove currency symbol if present
    const trimmedAmount = amountStr.replace("$", "").split(".");

    // Ensure the decimal part is defined
    const dollars = trimmedAmount[0];
    const cents = trimmedAmount[1] || '0';

    if (parseInt(cents, 10) > 0) {
        return titleCase(converter.toWords(dollars) + " dollars and " + converter.toWords(cents) + " cents");
    }

    return titleCase(converter.toWords(dollars) + " dollars and no cents");
}

export async function generateReceiptPDF(name, city, state, amount, receiptDate, donationDate, purpose, donationType) {
    const readableReceiptDate = new Date(receiptDate).toLocaleDateString("en-US");
    const readableDonationDate = new Date(donationDate).toLocaleDateString("en-US");
    const year = new Date(donationDate).getFullYear();

    const docDefinition = {
        pageMargins: [40, 25, 40, 100],
        background: {
            canvas: [{
                type: 'rect',
                x: 0, y: 0, w: 595.28, h: 841.89,
                color: '#fae7b5'
            }]
        },
        content: [
            {
                columns: [
                    { stack:
                            [{
                                text: STATIC_FIELDS.header1,
                                style: 'header'
                            },
                                {
                                    text: STATIC_FIELDS.subheader1,
                                    style: 'subheader'
                                }
                            ]},
                    {
                        image: logo,
                        width: 45,
                        height: 45,
                        alignment: 'center'
                    }
                ]
            },
            {
                canvas: [{ type: 'line', x1: 0, y1: 5, x2: 595-2*40, y2: 5, lineWidth: 3 }],
            },
            { text: ' ' },
            { text: STATIC_FIELDS.receiptDate + ': ' + readableReceiptDate, lineHeight: 3 },
            { text: name, style: 'bold' },
            { text: city + ', ' + state, lineHeight: 3 },
            { text: STATIC_FIELDS.messageBody1 },
            { text: ' ' },
            { text: STATIC_FIELDS.messageBody2 },
            { text: ' ' },
            { text: STATIC_FIELDS.messageBody3 },
            { text: ' ' },
            { text: STATIC_FIELDS.messageBody4 + year + ".", lineHeight: 5 },
            {
                layout: 'lightHorizontalLines',
                table: {
                    widths: [70, '*', '*', 75],
                    body: [
                        [STATIC_FIELDS.donationDate, STATIC_FIELDS.purpose, STATIC_FIELDS.donationTypes, STATIC_FIELDS.amount],
                        [readableDonationDate, purpose, donationType, amount],
                        ['', '', {text: STATIC_FIELDS.total, style: 'bold'}, {text: amount, style: 'bold'}]
                    ]
                }
            },
            { text: ' ' },
            { text: '**' + getTextFromAmount(amount) + '**', alignment: 'right' },
            { text: ' ', lineHeight: 10 },
            { text: STATIC_FIELDS.thanksMessage, style: 'thankyou' }
        ],
        footer: {
            stack: [
                { canvas: [{ type: 'line', x1: 0, y1: 5, x2: 595-2*40, y2: 5, lineWidth: 3 }] },
                { text: ' ', lineHeight: 0.5 },
                { text: STATIC_FIELDS.disclaimer, style: 'footer' }
            ],
            margin: [40, 25, 40, 100]
        }
    };

    // Replace spaces in the name with underscores for the filename
    const filename = `receipt_${name.replace(/\s+/g, '_')}.pdf`;

    // Create and download the PDF with the user's name in the filename
    pdfMake.createPdf(docDefinition).download(filename);
}
